class Solution {
public:
    void down(vector<int>& nums, int l, int r){
        int father = l, son = (father << 1) + 1;
        while(son <= r){
            if(son + 1 <= r and nums[son + 1] >= nums[son]) son++;
            if(nums[father] >= nums[son]) return;
            else swap(nums[father], nums[son]);
            father = son;
            son = (father << 1) + 1;
        }
    }
    void heapfity(vector<int>& nums, int l, int r){
        for(int i = (r >> 1) - 1; i >= l; i--){
            down(nums, i, r - 1);
        }
    }
    vector<int> inventoryManagement(vector<int>& nums, int cnt) {
        int m = nums.size();
        // build heap from first-cnt items 
        heapfity(nums, 0, cnt);

        for(int i = cnt; i < m ; i++){
            if(nums[i] >= nums[0]) continue;
            swap(nums[i], nums[0]);
            down(nums, 0, cnt - 1);
        }
        return vector<int>(nums.begin(), nums.begin() + cnt);
    }
};
