class Solution {
public:
    void down(vector<int>& nums, int left, int right){
        int father = left, son = (father << 1) + 1;
        while(son <= right){
            if(son + 1 <= right and nums[son] <= nums[son + 1]) son++;
            if(nums[father] >= nums[son]) return;
            swap(nums[father], nums[son]);
            father = son;
            son = (father << 1) + 1;
        }

    }
    void heapfiy(vector<int> &nums){
        int n = nums.size();
        for(int i = (n >> 1) - 1; i >= 0; i--){
            down(nums, i, n - 1);
        }
    }
    vector<int> sortArray(vector<int>& nums) {
        heapfiy(nums);
        int n = nums.size();
        for(int i = n - 1; i >= 1; i--){
            swap(nums[i], nums[0]);
            down(nums, 0, i - 1);
        }
        return nums; 
    }
};
