class Solution {
public:

    unordered_map<int,int> mp;
    int dp(int k, int n){
        if(k == 1) return n;
        if(n == 0) return 0;
        if(mp.count(n*100 + k)) return mp[100 * n + k];
        int ans = INT_MAX;
        int left = 1, right = n;
        while(left <= right){
            int mid = (left + right) >> 1;
            int low = dp(k-1, mid - 1);
            int high  = dp(k, n - mid);

            ans = min(ans, max(low, high) + 1);
            if(low <= high){
                left = mid + 1;
            }else right = mid - 1;
        }
        mp[100 * n + k] = ans;
        return ans;
    }
    int superEggDrop(int k, int n) {
        return dp(k, n);
    }
};
