class Solution {
public:
    int shortestSubarray(vector<int>& nums, int k) {
        int n = nums.size();
        vector<long> preSumArr(n + 1, 0);
        for(int i = 0; i < n; i++) preSumArr[i + 1] = nums[i] + preSumArr[i];
        int res = n + 1;
        deque<int> qu;
        for(int i = 0; i <= n; i++){
            long curSum = preSumArr[i];
            // j ____ i  å¦‚æœè¿™ä¸€æ®µå¯ä»¥æ„æˆï¼Œé‚£ä¹ˆä¹‹åjä¸å¯èƒ½å†ä½œä¸ºè¾¹ç•Œ
            while(not qu.empty() and preSumArr[i] - preSumArr[qu.front()] >= k){
                res = min(res, i - qu.front());
                qu.pop_front();
            }
            // j i å¦‚æœpre[i] < pre[j], é‚£ä¹ˆjå¯ä»¥ä½œä¸ºå·¦è¾¹ç•Œï¼Œé‚£ä¹ˆiè‚¯å®šå¯ä»¥å‘€ï¼Œæ‰€ä»¥ jä¸€å®šæ²¡ç”¨æœ‰äº†ã€‚ 
            while(not qu.empty() and curSum <= preSumArr[qu.back()]) qu.pop_back();
            qu.push_back(i);
        }
        return res < n + 1 ? res : -1;
    }
};
