//dfs
class Solution {
public:
    int m, n, ans = 0, num = 0;
    int x[4] = {1, 0, -1, 0}, y[4] = {0, 1, 0, -1};   

    int dfs(int px, int py, vector<vector<int>> &grid){
      if(px < 0 or px >= m or py < 0 or py >= n or not grid[px][py]) return 0;
      num++;
      grid[px][py] = 0;
      for(int i = 0; i < 4; i++){
        dfs(px + x[i], py + y[i], grid);
      }
      return num;
    }
    int maxAreaOfIsland(vector<vector<int>>& grid) {
      m = grid.size(), n = grid[0].size();
      for(int i = 0; i < m; i++){
        for(int j = 0; j < n; j++){
          if(grid[i][j]) {
            num = 0;
            ans = max(dfs(i, j, grid), ans); 
          }
        }
      }
      return ans;
    }
};
