class Solution {
public:
    int findMaxLength(vector<int>& nums) {
        unordered_map<int,int> mp;
        int m = nums.size();
        int ans = INT_MIN, cur = 0;
        mp[0] = -1;
        for(int i = 0; i < m; i++){
            cur += (nums[i] == 0 ? -1 : 1);
            if(mp.count(cur)){
                ans = max(ans, i - mp[cur]);
            } else{
                mp[cur] = i;
            }
        }
        return ans == INT_MIN ? 0 : ans;
    }
};
