class Solution {
public:

    int m;
    vector<string> split(const string &s, char ch){
        int i = 0, j = 0, m = s.size();
        vector<string> ret;
        while(j < m){
            while(i < m and s[i] == ch) i++;
            j = i;
            while(j < m and s[j] != ch) j++;
            if(i < m) ret.push_back(s.substr(i, j - i));
            i = j;
        }
        return ret;
    }
    bool isIPv4(string s){
        vector<string> nums = split(s, '.');
        if(nums.size() != 4) return false;
        int numberLen = 0;
        for(auto &num : nums){
            numberLen += num.size();
            if(num.size() > 1 and num[0] == '0')return false;
            for(auto &ch : num)  if(ch < '0' or ch > '9') return false;
            if(num.size() > 3 or stoi(num) > 255) return false;
        }
        if((int)s.size() - numberLen != 3) return false;
        return true;
    }

    bool isIPv6(string s){
        vector<string> nums = split(s, ':');
        if(nums.size() != 8) return false;
        int numberLen = 0;
        for(auto &num : nums){
            numberLen += num.size();
            if(num.size() > 4) return false;
            for(auto &ch : num){
                if((ch <= '9' and ch >= '0') or (ch <= 'f' and ch >= 'a') or (ch <= 'F' and ch >= 'A')) continue;
                else return false;
            }  
        }
        if((int)s.size() - numberLen > 7) return false;
        return true;  
    }
    string validIPAddress(string queryIP) {
        if(isIPv4(queryIP)) return "IPv4";
        else if(isIPv6(queryIP)) return "IPv6";
        else return "Neither";   
    }
};
