class Solution {
public:
    string ans;
    string decodeString(string s) {
        int n = s.size();
        if(s.size() == 0) return "";
        string temp;
        // cout << s << endl;
        // int times = 0;
        for(int i = 0; i < n; i++){
            if(s[i] <= 'z' and s[i] >= 'a') temp.push_back(s[i]);
            if(s[i] >= '0' and s[i] <= '9'){
                int times = 0;
                while(s[i] >= '0' and s[i] <= '9') times = times * 10 + s[i++] - '0';
                // cout << times << endl;
                int start = i + 1, end;
                i++; // start from '[' index
                int left = 1;
                while(left){
                    if(s[i] == ']') left--;
                    else if(s[i] == '[') left++;
                    i++;
                }
                end = i - 2;
                string in = decodeString(s.substr(start, end - start + 1));
                for(int k = 0; k < times; k++) temp += in;
                i--; // back to ']' index
            }
        }
        return temp;
    }
};
