class Solution {
public:
    string decodeString(string s) {
        int n = s.size();
        stack<pair<int,string>> st;
        if(n == 0) return "";
        string temp;
        int times = 0;
        for(int i = 0; i < n;i++){
            if(s[i] >= '0' and s[i] <= '9') times = times * 10 + s[i] - '0';
            else if(s[i] == '['){
                st.push({times, temp});
                times = 0;
                temp = "";
            } else if(s[i] == ']'){
                int time = st.top().first;
                string a = st.top().second;
                st.pop();
                while(time--) a = a + temp;
                temp = a;
            } else {
                temp.push_back(s[i]);
            }
        }
        return temp;

    }
};
