class Solution {
public:
    int coinChange(vector<int>& coins, int amount) {
        int m = coins.size();
        // int dp[amount + 1];
        vector<int> dp(amount + 1, INT_MAX);
        dp[0] = 0;
        for(int i = 0; i < m; i++){
            for(int j = coins[i]; j <= amount; j++){
                int need = j - coins[i];
                if(dp[need] == INT_MAX) continue;
                dp[j] = min(dp[j], dp[j-coins[i]] + 1); 
            }
        }
        return dp[amount] == INT_MAX ? -1 : dp[amount];
    }
};
