class Solution {
public:
    int lengthOfLIS(vector<int>& nums) {
        vector<int> t;
        t.push_back(INT_MIN);
        for(auto &num : nums){
            if(num > t.back()) t.push_back(num);
            else{
                auto it = lower_bound(t.begin(), t.end(), num);
                int idx = distance(t.begin(), it);
                t[idx] = num;
            } 
        }
        return t.size() - 1;
    }
};
