class MedianFinder {
public:
    priority_queue<int, vector<int>, greater<int>> pq2;
    priority_queue<int, vector<int>, less<int>> pq1;

    MedianFinder() {

    }

    void addNum(int num) {
        if(pq1.empty() or num < pq1.top()){
            pq1.push(num);
            if(pq1.size() - pq2.size() > 1){
                pq2.push(pq1.top());
                pq1.pop();
            }
        } else{
            pq2.push(num);
            if(pq2.size() - pq1.size() > 1){
                pq1.push(pq2.top());
                pq2.pop();
            }
        } 

    }

    double findMedian() {
        cout << pq1.size() << "  " << pq2.size() << endl;
        int m = pq1.size(), n = pq2.size();
        if((m + n) & 1){
            if(m > n) return 1.0 * pq1.top();
            else return 1.0 * pq2.top();
        }else return 1.0 * (pq1.top() + pq2.top()) / 2.0;
    }
};

/**
