class Solution {
public:
    vector<int> maxSlidingWindow(vector<int>& nums, int k) {
      // queue<int> q;

      int len = nums.size(), head = 0, tail = 0;
      vector<int> q(len+1, 0), ans;
      for(int i = 0; i < k-1; i++){
        while(head <= tail and q[tail] < nums[i]) tail--;
        q[++tail] = i;
      }

      for(int i = k-1; i < nums.size(); i++){
        while(head <= tail and q[tail] <= nums[i]) tail--;
        q[++tail] = i;
        while(q[head] <= i - k) head++;
        ans.push_back(nums[q[head]]);
      }
      return ans;
    }
};
