class Solution {
public:
    TreeNode* lowestCommonAncestor(TreeNode* root, TreeNode* p, TreeNode* q) {
      if(not root) return nullptr;
      if(root==p or root==q) return root;


      TreeNode* leftHave = lowestCommonAncestor(root->left, p, q);
      TreeNode* rightHave = lowestCommonAncestor(root->right, p, q);
      if(leftHave and rightHave) return root;
      else return leftHave ? leftHave : rightHave;

    }
};

