class Solution {
public:
    unordered_map<TreeNode*, TreeNode*> mp;
    void dfs(TreeNode* root){
      if(root->left) {mp[root->left] = root; dfs(root->left);}
      if(root->right){mp[root->right] = root; dfs(root->right);}
    }
    TreeNode* lowestCommonAncestor(TreeNode* root, TreeNode* p, TreeNode* q) {
      dfs(root);
      mp[root] = nullptr;
      unordered_set<TreeNode*> st;
      while(p){
        st.insert(p);
        p = mp[p];
      }
      while(q){
        if(st.count(q)) return q;
        else q = mp[q];
      }
      return nullptr;

    }
};
