class Solution {
public:
    TreeNode* invertTree(TreeNode* root) {
      if(not root) return root;
      queue<TreeNode*> q;
      q.push(root);
      while(not q.empty()){
        TreeNode* cur = q.front();
        q.pop();
        TreeNode* il = cur->left;
        cur->left = cur->right;
        cur->right = il;
        if(cur->left) q.push(cur->left);
        if(cur->right) q.push(cur->right);
      }
      return root;
    }
};


