class Solution {
public:
    //åˆ†ç»„
    int paritition(vector<int>& nums, int low, int high){
      int idx = (rand() % (high-low+1));
      swap(nums[low], nums[low+idx]);
      int pivot = nums[low];
      while(low < high){
        while(low < high and nums[high] <= pivot) --high;
        nums[low] = nums[high];
        while(low < high and nums[low] >= pivot) ++low;
        nums[high] = nums[low];
      }
      nums[low] = pivot;
      return low;
    }
    //å¿«æ’
    int quickSort(vector<int>& nums, int low, int high,int index){
      int q = paritition(nums, low, high);
      if(q == index) return nums[q]; 
      else  return q < index ? quickSort(nums, q+1, high, index) : quickSort(nums, low, q - 1, index);
    }
    int findKthLargest(vector<int>& nums, int k) {
      return quickSort(nums, 0, nums.size()-1, k-1);


    }
};
