class Solution {
public:
    int rob(vector<int>& nums) {
        int m = nums.size();
        vector<vector<int>> dp(m, vector<int>(2, 0));
        dp[0][0] = 0;
        dp[0][1] = nums[0];
        for(int i = 1; i < m; i++){
            dp[i][1] = dp[i-1][0] + nums[i];
            dp[i][0] = max(dp[i-1][0], dp[i-1][1]);
        }
        return max(dp[m-1][0], dp[m-1][1]);
    }
};
