/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* sortList(ListNode* head) {
      if(not head or not head->next) return head;
      int len = 0;
      ListNode* fake = new ListNode(-1,head), *first, *second, *cur = head, *prev;
      while(cur) len++, cur=cur->next;
      for (int subLength = 1; subLength < len; subLength <<= 1) {
        prev = fake, cur = fake->next;
        while(cur != nullptr){
          //åˆ†åˆ«æ‰¾åˆ°ä¸¤ä¸ªé“¾è¡¨
          first = cur;
          for(int i = 1; i < subLength and cur->next; i++) cur=cur->next;

          //è·å–æœ¬ç»„ç¬¬äºŒä¸ªé“¾è¡¨çš„èµ·å§‹èŠ‚ç‚¹
          second = cur->next, cur->next = nullptr, cur = second;
          for(int i = 1; i < subLength and cur and cur->next; i++) cur=cur->next;

          //è·å–ä¸‹ä¸€ä¸ªèŠ‚ç‚¹ï¼Œå¹¶æ–­å¼€è¿™ä¸€ç»„ä¸ä¸‹ä¸€ç»„çš„æŒ‡é’ˆ
          ListNode *next = nullptr;
          if(cur != nullptr) next = cur->next, cur->next = nullptr;

          //åˆå¹¶ä¸¤ä¸ªé“¾è¡¨
          ListNode *merged = merge(first, second);

          //é“¾æ¥è¿™ä¸€ç»„å’Œä¸Šä¸€ç»„ï¼Œå¹¶å°†prevæŒ‡å‘ä¸‹ä¸€ç»„çš„å‰ä¸€ä¸ªèŠ‚ç‚¹
          prev->next = merged;
          while(prev->next) prev=prev->next;

          //å°†curæŒ‡å‘ä¸‹ä¸€ç»„çš„ç¬¬ä¸€ä¸ªèŠ‚ç‚¹
          cur = next;
        }
      }
      return fake->next;
    }

    ListNode* merge(ListNode* la, ListNode* lb){
      ListNode *fake = new ListNode(-1), *cur = fake;
      while(la or lb){
        if(la and lb){
          if(la->val < lb->val) cur->next = la, la=la->next;
          else cur->next=lb, lb = lb->next;
        }else if(la) cur->next = la, la = la->next;
        else cur->next = lb, lb=lb->next;
        cur = cur->next;
      } 
      return fake->next;
    }
};
