    bool isSymmetric(TreeNode* root) {
        queue<TreeNode*> q;
        if(not root) return true;
        q.push(root->left);
        q.push(root->right);
        while(not q.empty()){
            TreeNode* left = q.front(); q.pop();
            TreeNode* right = q.front(); q.pop();
            if(not left and not right) continue;
            if(left and right){
                if(left->val != right->val) return false;
                q.push(left->left);
                q.push(right->right);
                q.push(left->right);
                q.push(right->left);
            }else return false;
        }
        return true;
    }
};
