class Solution {
public:
    bool isInterleave(string s1, string s2, string s3) {
        int m = s1.size(), n = s2.size();
        if(m + n != (int)s3.size()) return false;
        vector<vector<bool>> dp(m + 1, vector<bool>(n + 1, false));
        dp[0][0] = true;
        for(int i = 0; i <= m; i++){
            for(int j = 0;  j <= n; j++){
                if(i == 0 and j == 0) continue;
                int k = i + j - 1;
                if(i and s3[k] == s1[i-1]) dp[i][j] = dp[i][j] or dp[i-1][j];
                if(j and s3[k] == s2[j-1]) dp[i][j] = dp[i][j-1] or dp[i][j];
            }
        }
        return dp[m][n];
    }
};
