class Solution {
public:
    vector<vector<int>> ans;
    vector<int> temp;
    vector<bool> have;
    void dfs(vector<int>& nums, int cur){
        if(cur == nums.size()){
            ans.push_back(vector<int>(temp.begin(), temp.end()));
            return;
        }
        // insert
        temp.push_back(nums[cur]);
        have[cur] = true;
        dfs(nums, cur + 1);
        temp.pop_back();
        have[cur] = false;

        //not insert
        // if nums[cur-1], nums[cur] are same and have not selected nums[cur], nums[cur-1] must be not selected. 
        if(cur > 0 and nums[cur] == nums[cur-1] and have[cur-1]) return;
        dfs(nums, cur+1);
    }
    vector<vector<int>> subsetsWithDup(vector<int>& nums) {
        sort(nums.begin(), nums.end());
        int m = nums.size();
        have = vector<bool>(m, false);
        dfs(nums, 0);
        return ans;
    }
};

