class Solution {
public:
    int xx[4] = {0, 1, 0, -1}, yy[4] = {1, 0, -1, 0};
    int m,n;
    bool can = false;
    void backTrace(vector<vector<char>> &board, const string &word, int ci, int cj, int k){
        if(board[ci][cj] != word[k]) return;
        if(word.size() - 1 == k and board[ci][cj] == word.back()){
            can = true;
            return;
        }
        if(can) return;
        char old = board[ci][cj];
        board[ci][cj] = ' '; 
        for(int i = 0; i < 4; i++){
            int ni = xx[i] + ci, nj = yy[i] + cj;
            if(ni >= 0 and ni < m and nj >= 0 and nj < n) backTrace(board, word, ni, nj, k + 1);
        }
        board[ci][cj] = old;
    }
    bool exist(vector<vector<char>>& board, string word) {
        m = board.size(), n = board[0].size();
        for(int i = 0; i < m; i++){
            for(int j = 0; j < n; j++){
                backTrace(board, word, i, j, 0);
                if(can) return true;       
            }
        }
        return false;
    }
};
