class Solution {
public:
    unordered_map<char, int> need;
    unordered_map<char, int> mp;
    bool check(){
        for(auto &[ch, cnt] : need){
            if(mp[ch] < cnt) return false;
        }
        return true;
    }
    string minWindow(string s, string t) {
        int minL = INT_MAX;
        string ans = "";
        bool can = false;
        for(auto &ch : t) need[ch]++;
        int n = s.size();
        for(int i = 0, j = 0; j < n; j++){
            mp[s[j]]++;
            can = false;
            while(i <= j and check()){ 
                mp[s[i]]--;
                i++;
                can = true;
            }
            if(can){
                if(j - i + 2 < minL){
                    minL = j - i + 2;
                    ans = s.substr(i-1, j - i + 2);
                }
            }
        }
        return ans;
    }
};
