class Solution {
public:
    vector<vector<int>> generateMatrix(int n) {
        int cur = 1;
        vector<vector<int>> ans = vector<vector<int>>(n, vector<int>(n,0));
        int top = 0, bottom = n-1, left = 0, right = n-1;
        int i = 0, j = 0;
        while(cur <= n * n){
            for(int i = left; i <= right; i++)  ans[top][i] = cur++;
            ++top;
            for(int j = top; j <= bottom; j++) ans[j][right] = cur++;
            right--;
            for(int i = right; i >= left; i--)  ans[bottom][i] = cur++;
            bottom--;
            for(int j = bottom; j >= top; j--) ans[j][left] = cur++;
            left++;
        }
        return ans;
    }
};
