class Solution {
public:
    string multiply(string num1, string num2) {
        int m = num1.size(), n = num2.size();
        if(num1 == "0" or num2 == "0") return "0";
        reverse(num1.begin(), num1.end());
        reverse(num2.begin(), num2.end());
        vector<int> ans = vector<int>(m + n + 1, 0);
        int curBit = 0, carry = 0;
        for(int j = 0; j < n; j++){
            for(int i = 0; i < m; i++){
                int curVal = (num1[i] - '0') * (num2[j] - '0');
                ans[i+j] += (carry + curVal);
                carry = ans[i+j] / 10;
                ans[i+j] %= 10;
            }
            if(carry != 0) ans[j+m] += carry;
            carry = 0;
        }
        while(not ans.empty() and ans.back() == 0) ans.pop_back();
        string ret;
        for(auto num : ans) ret.push_back(num + '0');
        reverse(ret.begin(), ret.end());
        return ret;
    }
};
