        bool isFormer = target >= nums[0];
        while(l <= r){
            int mid = (l + r) >> 1;
            if(nums[mid] == target) return mid;
            else if(nums[mid] > target){
                // target mid | --- 
                if(isFormer){
                    r = mid - 1;
                }else{
                    // mid | target 
                    if(nums[mid] >= nums[0]) l = mid + 1;
