class Solution {
public:
    int longestValidParentheses(string s) {
        stack<int> st;
        int n = s.size();
        for(int i = 0; i < n; i++){
            if(s[i] == '(') st.push(i);
            else{
                if(st.empty()) s[i] = '0';
                else st.pop();
            } 
        }
        while(not st.empty()){
            s[st.top()] = '0';
            st.pop();
        }

        int ans = 0, i = 0, j = 0;
        while(j < n){
            i = j;
            while(i < n and s[i] == '0') i++;
            j = i;
            while(j < n and s[j] != '0') j++;
            // cout << j << i << endl;
            ans = max(ans, j - i);
        }
        // cout << s << endl;
        return ans;

    }
};
