class Solution {
public:
    int longestValidParentheses(string s) {
        int m = s.size();
        stack<pair<char,int>> st;
        // st.push(-1);
        int ans = 0;
        for(int i = 0; i < m; i++){
            if(s[i] == ')'){
                if(st.empty() or st.top().first == ')') st.push({')', i});
                else{
                    auto top = st.top(); 
                    if(top.first == '('){
                       st.pop();    
                       if(st.empty()) ans = max(ans, i + 1);
                       else ans = max(ans, i - st.top().second);
                    } 
                    // st.pop();
                }
            }else{
                st.push({'(', i});
            }
        }
        return ans;
    }
};

