 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* reverseKGroup(ListNode* head, int k) {
      ListNode* fake = new ListNode(-1, head), *lstart = fake, *start = fake, *end, *rend;
      int cnt = 0;
      while(true){
        cnt = 0;
        end = lstart;
        start = lstart->next;
        while(cnt < k and end->next) end = end->next, cnt++; //æ‰¾åˆ°end
        rend = end->next; //endä¸‹ä¸€ä¸ª
        if(cnt == k){   //å¦‚æœæ»¡è¶³k
          end->next = nullptr;  //æ–­å¼€å°¾èŠ‚ç‚¹
          lstart->next = reverse(lstart->next); //å‰å‰èŠ‚ç‚¹
          start->next = rend; //å°¾éƒ¨èŠ‚ç‚¹
        }else break;
        lstart = start;
      }
      return fake->next;
    }
    ListNode* reverse(ListNode* head){
      ListNode* pre = nullptr, *cur = head, *last;
      while(cur){
        last = cur->next;
        cur->next = pre;
        pre = cur;
        cur = last;
      }
      return pre;
    }
};
