class Solution {
public:
   ListNode* mergeListNode(ListNode* r1, ListNode* r2){
        if(not (r1 and r2)){
            return r1 ? r1 : r2;
        }
        ListNode* fake = new ListNode(), *cur  = fake, *c1 = r1, *c2 = r2;
        while(c1 and c2){
            if(c1->val > c2->val){
                cur->next = c2;
                c2=c2->next;
            }else{
                cur->next = c1;
                c1 = c1->next;
            }
            cur=cur->next;
        }
        cur->next = (c1 ? c1 : c2);
        return fake->next;
    }
    ListNode* mergeKLists(vector<ListNode*>& lists) {
        int len = lists.size();
        if(len == 0) return nullptr;
        for(int step = 1; step < len; step <<= 1){
            for(int start = step; start < len; start += (step << 1)){
                lists[start-step] = mergeListNode(lists[start-step], lists[start]);
            }
        }
        return lists[0];
    }
};

/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */

 // æœ€å°å †
class Solution {
public:
    ListNode* mergeKLists(vector<ListNode*>& lists) {
        priority_queue<pair<int, ListNode*>, vector<pair<int, ListNode*>>> pq;
        ListNode* fakeHead = new ListNode(-1), *cur = fakeHead;
        for(int i = 0; i < (int)lists.size(); i++){
            ListNode* cur = lists[i];
            if(cur) pq.push({-1 * cur->val, cur});
        }
        while(not pq.empty()){
            ListNode* minValNode = pq.top().second; pq.pop();
            cur->next = minValNode;
            if(minValNode->next) pq.push({-1 * minValNode->next->val, minValNode->next});
            cur = cur->next;
        }
        return fakeHead->next;
    }
};

