class Solution {
public:
    int myAtoi(string s) {
        int idx = 0, sign = 1;
        int n = s.size();
        // å¤„ç†æ— ç”¨ç©ºæ ¼
        while(idx < n and s[idx] == ' ') idx++;

        // å¤„ç†ç¬¦å·
        if(idx < n and (s[idx] == '-' or s[idx] == '+')){
            sign = (s[idx] == '-') ? -1 : 1;
            idx++;
        }

        int res = 0;
        // å¤„ç†æ•°å­—
        while(idx < n and isdigit(s[idx])){
            int digit = s[idx] - '0';
            // core code
            if(res > (INT_MAX - digit) / 10) return sign == 1 ? INT_MAX : INT_MIN;
            res = res * 10 + digit;
            ++idx;
        }
        return res * sign;
    }
};
