class Solution {
public:
    int reverse(int x) {
        int ans = 0;
        // cout << INT_MIN << " " << INT_MAX << endl;
        if(x == INT_MIN) return 0;
        int sign = x > 0 ? 1 : -1;
        x = abs(x);
        while(x){
            int curbit = x % 10;
            // ans * 10 + curbit >= INT_MIN ==> ans >= (INT_MIN - curbit) / 10
            if(ans > (INT_MAX - curbit) / 10) return 0;
            else ans = ans * 10 + curbit;
            x /= 10;
        }
        return sign * ans;
    }
};
