class Solution {
public:
    int lengthOfLongestSubstring(string s) {
      unordered_set<int> st;
      if(s.size() ==0)return 0;
      int ans = 1;
      for(int i = 0, j = 0; i < s.size(); i++){
        while(j < i and st.count(s[i])) st.erase(s[j++]); //æ‰¾åˆ°ç¬¬ä¸€ä¸ªæ»¡è¶³æƒ…å†µçš„
        st.insert(s[i]);
        ans = max(i - j + 1, ans);
      }
      return ans;
    }
};
